# Test generator

import re
from datetime import datetime

MATCH = 1
NOMATCH = 0

# These are the tests we generate functions for
# (re, input, expected, groups)
TESTS = [
  ("[^^]+", "abc", MATCH),
  ("[^^]+", "^", NOMATCH),
  ("[^al-obc]+", "kpd", MATCH),
  ("[^al-obc]+", "abc", NOMATCH),
  ("[al-obc]+", "almocb", MATCH),
  ("[al-obc]+", "defzx", NOMATCH),
  ("a(?:b|c|d)(.)", "ace", MATCH),
  ("a(?:b|c|d)*(.)", "ace", MATCH),
  ("a(?:b|c|d)+?(.)", "ace", MATCH)
]

FILE = open('src/re/test.rs', 'w')

OUTPUT = """
// This is an auto-generated test file
// Generated by src/test/test_generator.py
//
// Last Modified: %s

extern mod re;

macro_rules! run_tests(
  ($re: expr, $input: expr, $expect: pat) => (
    {
      let mut re = UncompiledRegexp::new($re);
      match re.run($input) {
        $expect => assert!(true),
        _ => assert!(false)
      }
    }
  )
)

#[cfg(test)]
mod python_tests {
  use re::UncompiledRegexp;

  // Tests start here
  %s
}
"""

TEST_FN = """
  #[test]
  fn test_case_ident_%s() {
    run_tests!(\"%s\", \"%s\", %s)
  }
"""

def generate_test_case(ident, regexp, input_str, expected):
  match = "Some(_)" if expected == 1 else "None"
  regexp = re.sub("\\\\", "\\\\", regexp)
  return TEST_FN % (ident, regexp, input_str, match)

if __name__ == "__main__":
  date = datetime.today().strftime("%B %d %Y %I:%M%p")
  buf = ""

  for (i, test) in enumerate(TESTS):
    buf += \
      generate_test_case(i, test[0], test[1], test[2])

  FILE.write(OUTPUT % (date, buf))

  print("Successfully generated test file: src/re/test.rs")
